// Extract audio and personality from the webhook body
const body = $input.first().json.body;
const audio = body.audio;           // base64-encoded WAV
const personality = body.personality || 'friendly';

// Personality configs â€” maps personality name to LLM + TTS settings
const personalities = {
  friendly: {
    system_prompt: "You are a friendly, helpful robot assistant named Robco. You speak in short, conversational sentences. Keep responses under 2-3 sentences. Be warm and enthusiastic.",
    temperature: 0.8,
    max_tokens: 150,
    voice_name: "en-US-Neural2-C",
    language_code: "en-US",
    speaking_rate: 1.0,
    pitch: 0.0
  },
  professional: {
    system_prompt: "You are a professional, efficient robot assistant named Robco. You speak clearly and concisely. Keep responses factual and under 2-3 sentences.",
    temperature: 0.6,
    max_tokens: 150,
    voice_name: "en-US-Neural2-D",
    language_code: "en-US",
    speaking_rate: 0.95,
    pitch: -2.0
  }
};

const config = personalities[personality] || personalities.friendly;

return [{
  json: {
    audio,
    personality,
    config
  }
}];

// Extract the text response from Gemini
const geminiResponse = $input.first().json;

let responseText = '';
try {
  // Gemini returns candidates[0].content.parts[0].text
  responseText = geminiResponse.body?.candidates?.[0]?.content?.parts?.[0]?.text
    || geminiResponse.candidates?.[0]?.content?.parts?.[0]?.text
    || '';
} catch (e) {
  responseText = 'I had trouble understanding that. Could you try again?';
}

if (!responseText) {
  responseText = 'I had trouble understanding that. Could you try again?';
}

// Get the personality config from previous node
const config = $('Prepare Request').first().json.config;

return [{
  json: {
    responseText,
    config
  }
}];

// Extract the audio content from Google TTS response
const ttsResponse = $input.first().json;

let audioBase64 = '';
try {
  audioBase64 = ttsResponse.body?.audioContent
    || ttsResponse.audioContent
    || '';
} catch (e) {
  // Return empty audio on error
}

const responseText = $('Extract Gemini Response').first().json.responseText;
const personality = $('Prepare Request').first().json.personality;

return [{
  json: {
    audio_base64: audioBase64,
    text: responseText,
    personality,
    timestamp: new Date().toISOString()
  }
}];
